mapper.select:
SELECT ISBN, TITLE, COVER, AUTHOR, PUBLISHER, PUBDATE, DESCRIPTION, STATUS, CATEGORY_NAME, CATEGORY_ID FROM (SELECT ROW_NUMBER() OVER(ORDER BY PUBDATE DESC) AS RN, ISBN, TITLE, COVER, AUTHOR, PUBLISHER, PUBDATE, DESCRIPTION, STATUS, CATEGORY_NAME, CATEGORY_ID FROM BOOK WHERE
TITLE LIKE '%' || #{st} || '%' OR AUTHOR LIKE '%' || #{st} || '%'
TITLE LIKE '%' || #{st} || '%'
AUTHOR LIKE '%' || #{st} || '%'
) WHERE RN BETWEEN #{begin} AND #{end}

SELECT ISBN, TITLE, COVER, AUTHOR, PUBLISHER, PUBDATE, DESCRIPTION, STATUS, CATEGORY_NAME, CATEGORY_ID
  FROM BOOK
 WHERE ISBN = ${isbn}

mapper.insert:
INSERT INTO BOOK_CHECKOUT(
  CHECKOUT_NO
, ISBN
, USER_NO
, STATUS
, CHECKOUT_DATE
) VALUES(
  BOOK_CHECKOUT_SEQ.NEXTVAL
, #{isbn}
, #{userNo}
, 0
, SYSDATE  
)
