mapper.insert:

SELECT ASK_SEQ.NEXTVAL
  FROM DUAL

INSERT INTO ASK(
  ASK_NO
   , ASK_TITLE
   , ASK_CONTENT
   , USER_NO
   , CREATED_DATE
   , MODIFIED_DATE
   , STATUS

  ) VALUES(
       #{askNo}
     , #{askTitle}
     , #{askContent}
     , #{userDto.userNo}
     , SYSDATE
     , SYSDATE
     , 0

    )

INSERT INTO ASK_IMAGE_T(
   ASK_NO
 , IMAGE_PATH
 , FILESYSTEM_NAME
   ) VALUES(
       #{askNo}
     , #{imagePath}
     , #{filesystemName}
      )

mapper.update:
UPDATE ASK
  SET HIT = HIT + 1
WHERE ASK_NO = #{askNo}

UPDATE ASK
   SET ASK_TITLE = #{askTitle}
     , ASK_CONTENT = #{askContent}
     , MODIFIED_DATE = SYSDATE
 WHERE ASK_NO = #{askNo}

mapper.select:
SELECT ASK_NO, IMAGE_PATH, FILESYSTEM_NAME
  FROM ASK_IMAGE_T
 WHERE ASK_NO = #{askNo}

mapper.update:
UPDATE ASK
   SET STATUS = 1
 WHERE ASK_NO = {askNo}
