mapper.insert:
INSERT INTO FACILITY(
  FAC_NO
, FAC_NAME
, FAC_CONTENT
) VALUES (
  FACILITY_SEQ.NEXTVAL
, #{facName}
, #{facContent}
)

INSERT INTO ATTACH_FAC(
  FAC_ATTACH_NO
, FAC_NO
, FAC_PATH
, FAC_ORIGINAL_FILENAME
, FAC_FILESYSTEM_NAME
, FAC_HAS_THUMBNAIL
) VALUES (
  ATTACH_FAC_SEQ.NEXTVAL
, FACILITY_SEQ.CURRVAL
, #{facPath}
, #{facOriginalFilename}
, #{facFilesystemName}
, #{facHasThumbnail}
)

INSERT INTO FAC_APPLY(
  FAC_APPLY_NO
, USER_NO
, FAC_NO
, FAC_REG_DATE
, FAC_START
, FAC_STATUS
) VALUES (
  FAC_APPLY_SEQ.NEXTVAL
, #{userNo}
, #{facNo}
, SYSDATE
, #{facStart}
, 0
)

mapper.select:
SELECT COUNT(*)
  FROM FAC_APPLY
 WHERE USER_NO = #{userNo}
   AND TO_CHAR(FAC_START, 'YYMMDD') = #{facStart}
