mapper.select:
SELECT FAC_NO, FAC_NAME, FAC_CONTENT
  FROM FROM FACILITY
 WHERE FAC_NO = #{facNo}

mapper.insert:
INSERT INTO FAC_APPLY(
  FAC_APPLY_NO
, USER_NO
, FAC_NO
, FAC_REG_DATE
, FAC_START
, FAC_STATUS
) VALUES (
  FAC_APPLY_SEQ.NEXTVAL
, #{userNo}
, #{facNo}
, SYSDATE
, #{facStart}
, 0
)

mapper.select:
SELECT COUNT(*)
  FROM FAC_APPLY
 WHERE USER_NO = #{userNo}
   AND TO_CHAR(FAC_START, 'YYMMDD') = #{facStart}
