mapper.select:
SELECT ISBN, TITLE, COVER, AUTHOR, PUBLISHER, PUBDATE, DESCRIPTION, STATUS, CATEGORY_NAME, CATEGORY_ID FROM (SELECT ROW_NUMBER() OVER(ORDER BY
TITLE ASC AUTHOR ASC PUBLISHER ASC PUBDATE ASC PUBDATE DESC
) AS RN, ISBN, TITLE, COVER, AUTHOR, PUBLISHER, PUBDATE, DESCRIPTION, STATUS, CATEGORY_NAME, CATEGORY_ID FROM BOOK WHERE
TITLE LIKE '%' || #{st} || '%' OR AUTHOR LIKE '%' || #{st} || '%'
TITLE LIKE '%' || #{st} || '%'
AUTHOR LIKE '%' || #{st} || '%'
) WHERE RN BETWEEN #{begin} AND #{end}
