mapper.select:
SELECT USER_NO, EMAIL, PW, NAME, MOBILE, GENDER, AGREE, STATE, AUTH,PW_MODIFIED_DATE, JOINED_DATE, STATUS
  FROM USER_T

  EMAIL = #{email}
  AND PW = #{pw}
  AND USER_NO = #{userNo}

mapper.update:
UPDATE USER_T
   SET NAME = #{name}
     , MOBILE = #{mobile}
     , GENDER = #{gender}
     , AGREE = #{agree}
 WHERE USER_NO = #{userNo}

UPDATE USER_T
   SET PW = #{pw}
     , PW_MODIFIED_DATE = SYSDATE
 WHERE USER_NO = #{userNo}
