mapper.insert:
 
 
   SELECT NOTICE_SEQ.NEXTVAL
     FROM DUAL
 
 INSERT INTO NOTICE (
     NT_NO
   , NT_TITLE
   , NT_CONTENT
   , USER_NO
   , NT_DATE
 ) VALUES (
     #{ntNo}
   , #{ntTitle}
   , #{ntContent}
   , #{userDto.userNo}
	  , TO_CHAR(SYSDATE, 'YYYY-MM-DD HH24:MI:SS')
 )

INSERT INTO ATTACH_NT (
    ATTACH_NT_NO
  , NT_PATH
  , NT_ORIGINAL_FILENAME
  , NT_FILESYSTEM_NAME
  , NT_DOWNLOAD_COUNT
  , NT_HAS_THUMBNAIL
  , NT_NO
) VALUES (
    ATTACH_NT_SEQ.NEXTVAL
  , #{ntPath}
  , #{ntOriginalFilename}
  , #{ntFilesystemName}
  , 0
  , #{ntHasThumbnail}
  , #{ntNo}
)
