mapper.insert:
 INSERT INTO NOTICE (
     NT_NO
   , NT_TITLE
   , NT_CONTENT
   , NT_DATE
 ) VALUES (
     NOTICE_SEQ.NEXTVAL
   , #{ntTitle}
   , #{ntContent}
	  , TO_CHAR(SYSDATE, 'YYYY-MM-DD HH24:MI')
 )

INSERT INTO ATTACH_NT (
    ATTACH_NT_NO
  , NT_PATH
  , NT_ORIGINAL_FILENAME
  , NT_FILESYSTEM_NAME
  , NT_DOWNLOAD_COUNT
  , NT_HAS_THUMBNAIL
  , NT_NO
) VALUES (
    ATTACH_NT_SEQ.NEXTVAL
  , #{ntPath}
  , #{ntOriginalFilename}
  , #{ntFilesystemName}
  , 0
  , #{ntHasThumbnail}
  , NOTICE_SEQ.CURRVAL
)

mapper.select:
SELECT ATTACH_NT_NO, NT_PATH, NT_ORIGINAL_FILENAME, NT_FILESYSTEM_NAME, NT_DOWNLOAD_COUNT, NT_HAS_THUMBNAIL, NT_NO
  FROM ATTACH_NT
 WHERE NT_NO = #{ntNo}

SELECT ATTACH_NT_NO, NT_PATH, NT_ORIGINAL_FILENAME, NT_FILESYSTEM_NAME, NT_DOWNLOAD_COUNT, NT_HAS_THUMBNAIL, NT_NO
  FROM ATTACH_NT
 WHERE ATTACH_NT_NO = #{attachNtNo}

mapper.update:
UPDATE ATTACH_NT
   SET NT_DOWNLOAD_COUNT = NT_DOWNLOAD_COUNT + 1
 WHERE ATTACH_NT_NO = #{attachNtNo}

UPDATE NOTICE
   SET NT_TITLE = #{ntTitle}
     , NT_CONTENT = #{ntContent}
 WHERE NT_NO = #{ntNo}
