mapper.insert:
INSERT INTO SCORE(
  ISBN
, USER_NO
, REVIEW_DATE
, STAR
, REVIEW
) VALUES(
  #{isbn}
, #{userNo}
, #{reviewDate}
, #{star}
, #{review}
)

mapper.select:
SELECT ISBN, USER_NO, REVIEW_DATE, STAR, REVIEW, GOOD
  FROM SCORE
 WHERE ISBN = #{isbn}

SELECT AVG(STAR) AS AVERAGE_STAR
FROM SCORE
WHERE ISBN = #{isbn}

mapper.delete:
DELETE FROM SCORE 
 WHERE ISBN=#{isbn}
   AND USER_NO=#{userNo}

mapper.select:
SELECT COUNT(*)
  FROM SCORE 
 WHERE ISBN=#{isbn}
   AND USER_NO=#{userNo}
